import requests

class GetImage:
    def get_product(self,vendor,index):
        limit=40
        skip=index*limit
        skip=str(skip)
        reponse=requests.get("http://apps.rminno.com/api/VendorProduct?limit=40&skip="+skip+"&vendorUniqueId="+vendor+"&productType=simple",headers={"token":""})
        return reponse